import { useState } from 'react';
import React from 'react';
import axios from 'axios';


const NoteList = ({ notes, deleteNote,updateNote }) => {

  const array = notes.map((item)=>{return item});

  const [notearray, setNoteArray] = useState(array);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`);
      deleteNote(id);
    } catch (error) {
      console.error(error);
    }
  };
  const handleUpdate = async (id,index) => {
    try {
      await axios.put(`http://localhost:5000/api/notes/${id}`,{text:notearray[index].text});
      updateNote(id);
    } catch (error) {
      console.error(error);
    }
  };

    const handleChange = (e,index)=> {
      const updatedData = [...notearray];
    
    // Modify the 'text' property of the specific index
    notearray[index].text = e.target.value;
    
    // Update the state variable with the modified array
    setNoteArray(notearray);
    }
  
  return (
    <div className="row gap-3" style={{padding:'15px',width:'100vw'}}>
      {array.map((note,index) => (
        <div className="col-lg-4 col-sm-6" key={index}  style={{
          margin:'0px',
          maxHeight:'200px',
          maxWidth:'430px',
          overflow:'scroll',
        border:'1px solid rgba(0, 0, 0, 0.2)',
        boxShadow:'0 4px 8px 0 rgba(0, 0, 0, 0.2)',
        padding:'10px 10px',borderRadius:'15px'}} >
          <textarea rows={7} cols={100} 
          onChange={(e)=>handleChange(e,index)}
          value={note.text}
          style={{fontSize:'15px',border:'none',
          fontFamily:'Helvetica,sans-serif',fontWeight:'500'}}>
            </textarea>
          
          <div className="text-muted small">Created at: {note.date}</div>
          <div style={{display:'flex',flexDirection:'row',alignItems:'center',gap:'10px',margin:'10px 0px'}}>
          <button
            className="btn btn-sm btn-danger"
            onClick={() => handleDelete(note.id)}
          >
            Delete
          </button>
          <button
            className="btn btn-sm btn-info"
            onClick={() => handleUpdate(note.id,index)}
          >
            Update
          </button>
          </div>
        </div>
      ))}  
    </div>
  );
};

export default NoteList;
