import React, { useState, useEffect } from 'react';
import NoteForm from './NoteForm';
import NoteList from './NoteList';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    fetchNotes(notes);
  }, [notes]);

  const fetchNotes = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/notes');
      setNotes(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // const addNote = async (note) => {
  //   try {
  //     const response = await axios.post('http://localhost:5000/api/notes', note);
  //     setNotes([...notes, response.data]);
  //   } catch (error) {
  //     console.error(error);
  //   }
  // };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`);
      setNotes(notes.filter((note) => note.id !== id));
    } catch (error) {
      console.error(error);
    }
  };

  const updateNote = async (id,index) => {
    try {
      await axios.put(`http://localhost:5000/api/notes/${id}`,{text:notes.text});
      setNotes(notes.filter((note,ind) => ind == index));
    } catch (error) {
      console.error(error);
    }
  };  

  return (
    <div className="container-fluid mt-4">
      <h1 className="mb-4">Sticky Note App</h1>
      <NoteForm/>
      <hr />
      <NoteList notes={notes} updateNote={updateNote} deleteNote={deleteNote}/>
    </div>
  );
};

export default App;
