import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import axios from 'axios';


const NoteList = ({ notes, deleteNote }) => {

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`);
      deleteNote(id);
    } catch (error) {
      console.error(error);
    }
  };

  const handlePopoverClick = (event) => {
    // Handle popover item click
    console.log('Popover item clicked:', event.target.textContent);
  };

  return (
    <div className="row gap-3" style={{padding:'15px',width:'100vw'}}>
      {notes.map((note,index) => (
        <div className="col-lg-4 col-sm-6" key={index}  style={{
          margin:'0px',
          maxHeight:'200px',
          maxWidth:'430px',
          overflow:'scroll',
        border:'1px solid rgba(0, 0, 0, 0.2)',
        boxShadow:'0 4px 8px 0 rgba(0, 0, 0, 0.2)',
        padding:'10px 10px',borderRadius:'15px'}} >
          <div style={{display:'flex',flexDirection:'row',alignItems:'center',justifyContent:'space-between'}}>
          <p style={{fontSize:'15px',fontFamily:'Helvetica,sans-serif',fontWeight:'500'}}>{note.text}</p>
          
          <div className="dropdown">
                  <button
                    className="btn btn-secondary btn-sm dropdown-toggle"
                    type="button"
                    id={`dropdownMenu${index}`}
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                  >
                    <i className="fas fa-ellipsis-v"></i>
                  </button>
                  <div
                    className="dropdown-menu"
                    aria-labelledby={`dropdownMenu${index}`}
                  >
                    <button
                      className="dropdown-item"
                      onClick={handlePopoverClick}
                    >
                      Option 1
                    </button>
                    <button
                      className="dropdown-item"
                      onClick={handlePopoverClick}
                    >
                      Option 2
                    </button>
                  </div>
                </div>

          </div>
          <div className="text-muted small">Created at: {note.date}</div>
          <button
            className="btn btn-sm btn-danger"
            onClick={() => handleDelete(note.id)}
          >
            Delete
          </button>
        </div>
      ))}  
    </div>
  );
};

export default NoteList;
