import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import axios from 'axios';
import {  } from '@fortawesome/free-solid-svg-icons';

const NoteList = ({ notes, deleteNote }) => {

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`);
      deleteNote(id);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="row mt-3" style={{padding:'10px'}}>
    <div className="row" style={{gap:'5px'}}>
      {notes.map((note,index) => (
        <div className="col-lg-4" key={index}  style={{border:'1px solid rgba(0, 0, 0, 0.2)',
        boxShadow:'0 4px 8px 0 rgba(0, 0, 0, 0.2)',
        padding:'10px 10px',borderRadius:'15px'}} >
          <div style={{display:'flex',flexDirection:'row',alignItems:'center',justifyContent:'space-between'}}>
          <p style={{fontSize:'15px',fontFamily:'Helvetica,sans-serif',fontWeight:'500'}}>{note.text}</p>
          <button type="button" className="btn btn-lg btn-danger" data-bs-toggle="popover" data-bs-title="Popover title" data-bs-content="And here's some amazing content. It's very engaging. Right?">Click to toggle popover</button>
          </div>
          <div className="text-muted small">Created at: {note.date}</div>
          <button
            className="btn btn-sm btn-danger"
            onClick={() => handleDelete(note.id)}
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  
    </div>
  );
};

export default NoteList;
