import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const NoteForm = ({addNote}) => {
  const [noteText, setNoteText] = useState('');

  const generateUniqueId = ({addNote}) => {
    const timestamp = new Date().getTime();
    const random = Math.random().toString(36).substring(2);
    return `${timestamp}-${random}`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (noteText.trim() !== '') {
      try {
        const id = parseInt(generateUniqueId()); // Generate a unique ID
        const note = {
          id,text: noteText.trim(),
          date:`${new Date().getHours() > 12 ? new Date().getHours()-12:new Date().getHours()}:${new Date().getMinutes()}:${new Date().getSeconds()} ${new Date().getHours() > 12 ? 'PM' : 'AM'}`.trim()};
          await addNote(note)
          setNoteText('');
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="input-group mb-3">
        <textarea
          className="form-control"
          placeholder="Enter note"
          value={noteText}
          rows={5}
          onChange={(e) => setNoteText(e.target.value)}
        />
      </div>
      <button className="btn btn-primary" type="submit">
          Add Note
        </button>
    </form>
  );
};

export default NoteForm;
