import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import axios from 'axios';
import { faDotCircle } from '@fortawesome/free-solid-svg-icons';

const NoteList = ({ notes, deleteNote }) => {

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`);
      deleteNote(id);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container row" style={{gap:'20px'}}>
      {notes.map((note) => (
        <div className="col-sm-4 col-lg-4" 
        style={{border:'1px solid rgba(0, 0, 0, 0.2)',
        boxShadow:'0 4px 8px 0 rgba(0, 0, 0, 0.2)',
        padding:'10px 10px',borderRadius:'15px'}} 
        key={note.id}>
            {console.log(note.id)}
          <div>
          <p style={{fontSize:'15px',fontFamily:'Helvetica,sans-serif',fontWeight:'500'}}>{note.text}</p>
          <button type="button" class="btn btn-secondary" data-bs-container="body" data-bs-toggle="popover" data-bs-placement="left" data-bs-content="Left popover">
          <faDotCircle/>
          </button>
          </div>
          <div className="text-muted small">Created at: {note.date}</div>
          <button
            className="btn btn-sm btn-danger"
            onClick={() => handleDelete(note.id)}
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default NoteList;
